var navbar=document.getElementById("top-bar"),navTop=navbar.offsetTop;function stickyNavigation(){window.scrollY>navTop?navbar.classList.add("header-fixed"):navbar.classList.remove("header-fixed")}function animateScrollNavbar(e){e.preventDefault();var n=this.getAttribute("href").replace("#","");animateScroll(document.getElementById(n).getBoundingClientRect().top)}function animateScroll(n){n=document.body.scrollHeight-window.innerHeight>n+scrollY?n:document.body.scrollHeight-window.innerHeight;var t=window.scrollY,i=90,o=Math.PI/i,a=0;requestAnimationFrame(function e(){a<i&&(requestAnimationFrame(e),a++,window.scrollTo(0,t+.25*n*Math.pow(1-Math.cos(o*++a),2)))})}window.addEventListener("scroll",stickyNavigation);for(var links=document.querySelectorAll(".scroll"),i=0,linksLength=links.length;i<linksLength;i++)links[i].onclick=animateScrollNavbar;